import React, { Component } from "react";
import { Link } from "react-router-dom";
import projects from "../projects";
import ImageGallery from "react-image-gallery";
import "../projects.scss";
import { Player } from "video-react";
import "../styles/scss/video-react.scss"; // or import scss
import ArchVid from "../Assets2/Archrenderings/arch2.mp4";

function scroll() {
  // var imagebox = document.getElementById("imagebox");
  // imagebox.scrollTop = 0;
  console.log("hajj");
}

export default class Texts extends Component {
  onClick(event) {
    this.addSentence1();
    this.addScrollPos();
  }

  constructor(props) {
    super(props);

    this.state = {
      projects: projects
    };
    this.imageCounter = this.imageCounter.bind(this);
  }

  componentDidMount() {
    // subscribe state change
    this.player.subscribeToStateChange(this.handleStateChange.bind(this));

    this.player.state = {
      width: "200px",
      height: 0
    };
  }

  imageCounter() {
    let counter = this.state.counter;
    this.setState({
      counter: counter + 1
    });
    console.log("Hjajjjsj", this.state.counter);
  }

  render() {
    const myProjects = this.state.projects;

    return (
      <div>
        <div className="projectIntro">
          <h1>Swim around or deep dive?</h1>
          <p>
            Some of the projects doesnt need a lot of explanation. It might just
            be a poster. But some of the projects needs a bit time to make
            sense. Here you choose if you want to swim around on the surface
            level or dive in to it
          </p>
        </div>
        <div className="desktop">
          {" "}
          {myProjects.map(eachProject => {
            if (eachProject.type === "image") {
              return (
                <div className="desktopbox" key={eachProject.id}>
                  <div
                    onClick={scroll}
                    className="mobiletitle"
                    to={`/projects/${eachProject.id}`}
                  >
                    <ImageGallery
                      className="imageGallery"
                      showFullscreenButton={true}
                      showPlayButton={false}
                      showThumbnails={false}
                      slideDuration="0"
                      items={eachProject.imageM}
                    />
                    {/* <img
                    className="mobileHero"
                    src={eachProject.image[0]}
                    alt="haj"
                  /> */}
                    <div className="textBox">
                      <div className="titlebox">{eachProject.name}</div>
                      <div className="descbox">{eachProject.description}</div>
                    </div>
                  </div>
                </div>
              );
            } else {
              return (
                <div className="desktopbox" key={eachProject.id}>
                  <div
                    onClick={scroll}
                    className="mobiletitle"
                    to={`/projects/${eachProject.id}`}
                  >
                    <Player
                      playsInline
                      poster="/assets/poster.png"
                      src={eachProject.video}
                      ref={player => {
                        this.player = player;
                      }}
                      autoPlay
                      muted
                      loop
                    />
                    {/* <iframe
                      src={eachProject.video}
                      width="100%"
                      height="264"
                      frameborder="0"
                      allow="autoplay; fullscreen"
                      allowfullscreen
                    ></iframe> */}

                    <div className="textBox">
                      <div className="titlebox">{eachProject.name}</div>
                      <div className="descbox">{eachProject.description}</div>
                    </div>
                  </div>
                </div>
              );
            }
          })}
        </div>
      </div>
    );
  }
}
