import Plant from "../Assets/plant.png";
import Draggable from "react-draggable";

import React from "react";
import { Link } from "react-router-dom";
import projects from "../projects";

import ReactCardFlip from "react-card-flip";

class Projects extends React.Component {
  state = {
    projects: projects
  };

  constructor() {
    super();
    this.state = {
      isFlipped: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    e.preventDefault();
    this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
  }

  render() {
    const myProjects = this.state.projects;
    return (
      <div className="leftContainer">
        <div>
          {myProjects.map(eachProject => {
            return (
              <div className="projectTitleBox" key={eachProject.id}>
                <Link
                  className={eachProject.titleClass}
                  to={`/projects/${eachProject.id}`}
                >
                  {eachProject.name}
                </Link>

                <hr />
              </div>
            );
          })}
        </div>
        <ReactCardFlip
          isFlipped={this.state.isFlipped}
          flipDirection="vertical"
        >
          <YOUR_FRONT_CCOMPONENT key="front">
            This is the front of the card.
            <button onClick={this.handleClick}>Click to flip</button>
          </YOUR_FRONT_CCOMPONENT>

          <YOUR_BACK_COMPONENT key="back">
            This is the back of the card.
            <button onClick={this.handleClick}>Click to flip</button>
          </YOUR_BACK_COMPONENT>
        </ReactCardFlip>
      </div>
    );
  }
}

export default Projects;
