import React, { Component } from "react";

var tempX = 0;
var tempY = 0;

export default class Blurback extends Component {
  componentDidMount() {
    function getMouseXY(e) {
      tempX = e.pageX;
      tempY = e.pageY;

      let divstyle = {
        left: tempX + "px",
        top: tempY + "px"
      };
      console.log(tempY, tempX, divstyle);

      if (tempX < 0) {
        tempX = 0;
      }
      if (tempY < 0) {
        tempY = 0;
      }
    }
    document.querySelector(".circle").style.left = tempX + "px";
    document.getElementById("chartdiv");
    document.querySelector(".circle").style.top = tempY + "px";
    document.addEventListener("mousemove", getMouseXY);
  }

  render() {
    return (
      <div>
        <div className="circle" id="chartdiv" />
      </div>
    );
  }
}
