import React, { Component } from "react";
import { Link } from "react-router-dom";
import projects from "../projects";
function scroll() {
  // var imagebox = document.getElementById("imagebox");
  // imagebox.scrollTop = 0;
  console.log("hajj");
}

export default class Texts extends Component {
  onClick(event) {
    this.addSentence1();
    this.addScrollPos();
  }

  constructor(props) {
    super(props);

    this.state = {
      projects: projects,
      counter: 0
    };
    this.imageCounter = this.imageCounter.bind(this);
  }

  imageCounter() {
    this.state.counter += 1;
  }

  render() {
    const myProjects = this.state.projects;

    return (
      <div className="mobile">
        <div>
          {myProjects.map(eachProject => {
            return (
              <div className="mobtitlebox" key={eachProject.id}>
                <div
                  onClick={scroll}
                  className="mobiletitle"
                  to={`/projects/${eachProject.id}`}
                >
                  <img
                    onClick={this.imageCounter}
                    className="mobileHero"
                    src={eachProject.image[this.state.counter]}
                  />
                  <div className="textBox">
                    <div>{eachProject.name}</div>
                    <div>{eachProject.description}</div>
                  </div>
                </div>

                <hr />
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
