import React, { Component } from "react";

export default class Blurback extends Component {
  componentDidMount() {
    var tempX = 0;
    var tempY = 0;
    function getMouseXY(e) {
      tempX = e.pageX;
      tempY = e.pageY;

      let divstyle = {
        left: tempX + "px",
        top: tempY + "px"
      };
      console.log(tempY, tempX, divstyle);

      if (tempX < 0) {
        tempX = 0;
      }
      if (tempY < 0) {
        tempY = 0;
      }
    }

    const changeDiameter = e => {
      document.getElementById("chartdiv").style.left = e.pageX - 50 + "px";
      document.getElementById("chartdiv").style.top = e.pageY - 50 + "px";
    };

    window.addEventListener("mousemove", changeDiameter);
  }

  render() {
    return (
      <div>
        <div className="circle" id="chartdiv" />
      </div>
    );
  }
}
