import React from "react";
import { Link } from "react-router-dom";
import projects from "../projects";
import Draggable from "react-draggable";
import testimg from "../Assets/Grimeton/1.png";
import List from "../components/Projects";

const ProjectDetails = props => {
  console.log(props, projects);

  const projectId = props.match.params.projectId;

  let foundProject = projects.find(project => {
    //   if (project.id === projectId) return true
    return project.id === projectId;
  });

  let image = foundProject.image;
  console.log(image);
  console.log(testimg);

  return (
    <div>
      <List />
      <div className="pInfo">
        <h2>{foundProject.name} </h2>
        <span style={{ fontSize: "14px" }}>{foundProject.year}</span>
        <h3>{foundProject.technologies}</h3>
      </div>
      <div className="imagebox">
        {image.map(eachImage => {
          return (
            <div>
              <img className="pImage" src={eachImage} />
              <hr />
            </div>
          );
        })}
        <iframe
          className="resp-iframe"
          src={foundProject.video}
          width="100%"
          frameborder="0"
          allow="autoplay; fullscreen"
          allowfullscreen
        />
      </div>

      <Draggable className="test">
        <div className="descriptionbox">
          <p>{foundProject.description}</p>
        </div>
      </Draggable>

      {/* <Link to="/projects">Back</Link> */}
    </div>
  );
};

export default ProjectDetails;
