import React, { Component } from "react";
import { Link } from "react-router-dom";
import projects from "../projects";
import ImageGallery from "react-image-gallery";

const ProjectDetails = props => {
  console.log(props, projects);

  const projectId = props.match.params.projectId;

  let foundProject = projects.find(project => {
    //   if (project.id === projectId) return true
    return project.id === projectId;
  });
};
function scroll() {
  // var imagebox = document.getElementById("imagebox");
  // imagebox.scrollTop = 0;
  console.log("hajj");
}

export default class Texts extends Component {
  onClick(event) {
    this.addSentence1();
    this.addScrollPos();
  }

  constructor(props) {
    super(props);

    this.state = {
      projects: projects
    };
    this.imageCounter = this.imageCounter.bind(this);
  }

  imageCounter() {
    let counter = this.state.counter;
    this.setState({
      counter: counter + 1
    });
    console.log("Hjajjjsj", this.state.counter);
  }

  render() {
    const myProjects = this.state.projects;

    return (
      <div className="mobile">
        <div>
          {myProjects.map(eachProject => {
            return (
              <div className="mobtitlebox" key={eachProject.id}>
                <div
                  onClick={scroll}
                  className="mobiletitle"
                  to={`/projects/${eachProject.id}`}
                >
                  {/* <img
                    className="mobileHero"
                    src={eachProject.image[this.state.counter]}
                    alt="haj"
                  /> */}
                  <ImageGallery
                    showFullscreenButton={false}
                    showPlayButton={false}
                    showThumbnails={false}
                    slideDuration="0"
                    items={foundProject.imageM}
                  />
                  <div className="textBox">
                    <div>{eachProject.name}</div>
                    <div>{eachProject.description}</div>
                  </div>
                </div>

                <hr />
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
