import React, { Component } from "react";
import $ from "jquery";

export default class Blurback extends Component {
  componentDidMount() {
    function moveDivs(event) {
      x = event.screenX;
      y = event.screenY;

      //   document.getElementById("suddis").style.left = at (x - 200 + "px");
      document.querySelector(".psudd2").style.clipPath =
        "circle(80px at " + (x - 120) + "px " + (y - 150) + "px)";
    }
  }
  render() {
    return (
      <div>
        <div id="chartdiv" class="blur" />
      </div>
    );
  }
}
