import React, { Component } from "react";
import { Link } from "react-router-dom";
import projects from "../projects";
import ImageGallery from "react-image-gallery";
import "../projects.scss";
import { Player } from "video-react";
import "../styles/scss/video-react.scss"; // or import scss
import ArchVid from "../Assets2/Archrenderings/arch2.mp4";

function scroll() {
  // var imagebox = document.getElementById("imagebox");
  // imagebox.scrollTop = 0;
  console.log("hajj");
}

export default class Texts extends Component {
  onClick(event) {
    this.addSentence1();
    this.addScrollPos();
  }

  constructor(props) {
    super(props);

    this.state = {
      swim: "swim",
      projects: projects
    };

    this.swimSwitch = this.swimSwitch.bind(this);
    this.swimSwitchDeep = this.swimSwitchDeep.bind(this);
    this.swimSwitchShallow = this.swimSwitchShallow.bind(this);
  }

  swimSwitch() {
    this.setState({
      swim: "both"
    });
  }
  swimSwitchShallow() {
    this.setState({
      swim: "swim"
    });
  }
  swimSwitchDeep() {
    this.setState({
      swim: "deepdive"
    });
  }

  render() {
    const myProjects2 = this.state.projects;

    const myProjects = myProjects2.filter(
      project => project.filter === this.state.swim
    );

    console.log();

    return (
      <div>
        <div className="projectIntro">
          <button onClick={this.swimSwitchShallow}>
            Swim around or deep dive?
          </button>
          <button onClick={this.swimSwitchDeep}>
            Some of the projects doesnt need a lot of explanatio
          </button>
        </div>
        <div className="desktop">
          {" "}
          {myProjects.map(eachProject => {
            if (eachProject.type === "image") {
              return (
                <div className="desktopbox" key={eachProject.id}>
                  <div
                    onClick={scroll}
                    className="mobiletitle"
                    to={`/projects/${eachProject.id}`}
                  >
                    <ImageGallery
                      className="imageGallery"
                      showFullscreenButton={true}
                      showPlayButton={false}
                      showThumbnails={false}
                      slideDuration="0"
                      items={eachProject.imageM}
                    />

                    <div className="textBox">
                      <div className="titlebox">{eachProject.name}</div>
                      <div className="descbox">{eachProject.description}</div>
                    </div>
                  </div>
                </div>
              );
            } else {
              return (
                <div className="desktopbox" key={eachProject.id}>
                  <div
                    onClick={scroll}
                    className="mobiletitle"
                    to={`/projects/${eachProject.id}`}
                  >
                    <Player
                      playsInline
                      poster="/assets/poster.png"
                      src={eachProject.video}
                      ref={player => {
                        this.player = player;
                      }}
                      autoPlay
                      muted
                      loop
                    />
                    {/* <iframe
                      src={eachProject.video}
                      width="100%"
                      height="264"
                      frameborder="0"
                      allow="autoplay; fullscreen"
                      allowfullscreen
                    ></iframe> */}

                    <div className="textBox">
                      <div className="titlebox">{eachProject.name}</div>
                      <div className="descbox">{eachProject.description}</div>
                    </div>
                  </div>
                </div>
              );
            }
          })}
        </div>
      </div>
    );
  }
}
